mapper.insert:
INSERT INTO Artist (
    artistName
)
VALUES
(
    #{artist.artistName}
)

    SELECT LAST_INSERT_ID() AS artistId

INSERT INTO Live (
    liveName,
    artistId
)
VALUES
(
    #{live.liveName},
    #{artistId}
)

    SELECT LAST_INSERT_ID() AS liveId

mapper.select:
SELECT EXISTS (
     SELECT
         liveScheduleId
     FROM
         LiveSchedule
     WHERE
         liveId = #{liveId}
     AND liveDate = #{liveDate}
)
